<?php/** * The Template for displaying single posts of portfolio with pagination items (detailed project). * * @package studiopaul * @since studiopaul 1.0 */get_header(); ?><!-- Subheader ================================================== --><div id="subheader" class="inshadow page">	<div class="row">		<div class="twelve columns">			<p class="left">				 PORTFOLIO			</p>			<p class="right">				 <?php echo get_post_meta($post->ID, 'sp_pagedesc', true); ?>			</p>		</div>	</div></div><div class="hr"></div><!-- Portfolio Detailed Project ================================================== --><?php while ( have_posts() ) : the_post(); ?>
